import React,{Component} from 'react';

import './Styles/Weather.css';


class Weather extends Component{
    constructor(props){
super(props);
this.state={
    errorMsg:false,
    data:null,
}
    }
    
    submitPlace(event){
        event.preventDefault();        
        let value=this.refs.input.value;
        let fetchData=fetch(`http://api.openweathermap.org/data/2.5/weather?q=${value}&APPID=2cf59e3c2ba338440a4e32992b4a76e7`);
        fetchData.then((data1)=>{
            console.log("data1",data1);

           
            
            return data1.json();
        }).then((data2)=>{
            if(data2.cod === '404'){
                this.refs.list.innerHTML=' ';
                this.refs.errorMsgg.innerHTML="<span>Please Enter valid area ....! </span>";
                this.setState(prevState=>{
                    return {errorMsg:!prevState.errorMsg}
                })
            }
            this.setState({errorMsg:!this.state.errorMsg});
            this.refs.errorMsg.innerHTML=" ";
        
        }).catch();

    }

   
    render(){
        
        
        return(
     <div id="wrapper">
            <div id="container">
            <h1>Weather Report</h1>
          <form onSubmit={(e)=>this.submitPlace(e)}>
           
           <input ref="input" type="text" className="input" placeholder="Please enter area name"/>
           <input type="submit" className="submit"/>
           </form>
           <div ref="errorMsgg" className="error">
        </div>
            <div>
            <ul id="listItems" ref="list">
                               <li>Please enter some valid area code to get the weather details</li>
                               </ul>
                        </div>
           </div>
     </div>
        )  
    }
}


export default Weather;
