import React,{Component} from 'react';

import './Styles/Weather.css';


class Weather extends Component{
    constructor(props){
super(props);
this.state={
    value:null,
    errorMsg:false,
}
    }
    
    submitPlace(event){
        event.preventDefault();        
        let value=this.refs.input.value;
        let fetchData=fetch(`http://api.openweathermap.org/data/2.5/weather?q=${value}&APPID=2cf59e3c2ba338440a4e32992b4a76e7`);
        fetchData.then((data1)=>{
            console.log("data1",data1)
            return data1.json();
        }).then((data2)=>{
            console.log('data2',data2.cod);
            if(data2.cod === '404'){
                throw data2;
            }
        
        }).catch(error=>{
            this.setState({errorMsg:error.message,});
           console.log(error.message) 
        });



    }
    render(){
        return(
     <div id="wrapper">
            <div id="container">
            <h1>Weather Report</h1>
          <form onSubmit={(e)=>this.submitPlace(e)}>
           
           <input ref="input" type="text" className="input" placeholder="Please enter area name"/>
           <input type="submit" className="submit"/>
           </form>
           <div className="error">
           {this.state.errorMsg&&<span>Please Enter valid area ....! </span>}
           </div>
           <div>
               <ul id="listItems">
                   
               </ul>
           </div>
           </div>
     </div>
        )  
    }
}


export default Weather;
