import React,{Component} from 'react';



class Weather extends Component{
    constructor(props){
        super(props);
        this.state={

        }
    }




onChange(){
    console.log(this.search.value)
}

handleSubmit(){
    const myLocation=this.search.value;

    let api=`http://api.openweathermap.org/data/2.5/weather?q=${myLocation}&APPID=2cf59e3c2ba338440a4e32992b4a76e7`;
    fetch(api)
    .then(response=>response.json())
    .then(data=>console.log(data))
    .catch(error=>console.log(error));
}








    render(){
        return (
            <div className="container">
            <form onSubmit={()=>this.handleSubmit()}>
                <input onChange={()=>this.onChange()} type="text" placeholder="Enter the place..." ref={input=>this.search=input}></input>
                <button type="submit">Submit</button>
            </form>
            </div>
        )
    }
}

export default Weather;
