import React,{Component} from 'react';

import './Styles/Weather.css';




class Weather extends Component{
    constructor(props){
super(props);
this.state={
    errorMsg:false,
    data:null,
}
    }
    
    getData= (value)=>{
        const api=`http://api.openweathermap.org/data/2.5/weather?q=${value}&APPID=2cf59e3c2ba338440a4e32992b4a76e7`;
        fetch(api)
        .then(
          response=> response.json())
        .then((data)=>{
            console.log(data)
        });
    }

    componentDidUpdate(){
        this.getData('bothell')
    }

   
    render(){
        
        return(
     <div id="wrapper">
            <div id="container">
            <h1>Weather Report</h1>
          <form onSubmit={(e)=>this.submitPlace(e)}>
           
           <input ref="input" type="text" className="input" placeholder="Please enter area name"/>
           <input type="submit" className="submit"/>
           </form>
           

        
      {this.state.errorMsg ? (
          <div ref="errorMsgg" className="error">enter valid data...!
          </div>
      ) : null}
      
            <div>
            <ul id="listItems" ref="list">
            
                               </ul>
                        </div>
    
        </div>



            
     </div>
        )  
    }
}


export default Weather;
